mapper.select:
select a.* from employ a where 1=1

	and a.realname = #{realname}


	and a.sex = #{sex}


	and a.birthday = #{birthday}


	and a.idcard = #{idcard}


	and a.jiguan = #{jiguan}


	and a.minzu = #{minzu}


	and a.workdate = #{workdate}


	and a.contact = #{contact}


	and a.memo = #{memo}
